package ru.miit;import javax.validation.ConstraintViolation;import javax.validation.Validation;import javax.validation.Validator;import javax.validation.ValidatorFactory;import java.util.Set;public class App {    private static void validateRollingStock(RollingStock rollingStock) {        ValidatorFactory factory = Validation.buildDefaultValidatorFactory();        Validator validator = factory.getValidator();        Set<ConstraintViolation<RollingStock>> violations = validator.validate(rollingStock);        for (ConstraintViolation<RollingStock> violation : violations) {            System.out.println(rollingStock);            System.out.println(violation.getMessage() + "\n");        }    }    public static void main(String[] args) {        RollingStock rollingStock1 = new RollingStock(12, 100, 6);        RollingStock rollingStock2 = new RollingStock(12, 100, 1);        RollingStock rollingStock3 = new RollingStock(12, 99, 0);        RollingStock rollingStock4 = new RollingStock(12, 100, 6);        validateRollingStock(rollingStock1);        validateRollingStock(rollingStock2);        validateRollingStock(rollingStock3);        validateRollingStock(rollingStock4);    }}